import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import './App.css';

const API_KEY = 'YOUR_API_KEY'; // Replace with your actual OpenWeather API key

const Container = styled.div`
  max-width: 400px;
  margin: 0 auto;
  padding: 20px;
  text-align: center;
`;

const Heading = styled.h2`
  font-size: 24px;
  margin-bottom: 20px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  background-color: #3498db;
  color: #fff;
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #2980b9;
  }
`;

const WeatherInfo = styled.div`
  margin-top: 20px;
`;

const City = styled.h3`
  font-size: 20px;
  margin-bottom: 10px;
`;

const Temperature = styled.p`
  font-size: 16px;
  margin-bottom: 5px;
`;

const Description = styled.p`
  font-size: 16px;
  margin-bottom: 5px;
`;

const Weather = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);

  const fetchWeather = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Container>
      <Heading>Weather App</Heading>
      <Input
        type="text"
        placeholder="Enter a city"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <Button onClick={fetchWeather}>Get Weather</Button>
      {weatherData && (
        <WeatherInfo>
          <City>{weatherData.name}</City>
          <Temperature>Temperature: {weatherData.main.temp}Â°C</Temperature>
          <Description>Weather: {weatherData.weather[0].description}</Description>
        </WeatherInfo>
      )}
    </Container>
  );
};

export default Weather;
